client/src/pages/MensProducts.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { keepPreviousData } from '@tanstack/react-query';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Star, Heart, Search } from 'lucide-react';
import { 
  Pagination,
  PaginationContent,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious
} from '@/components/ui/pagination';
import TopNavigationBar from '@/components/TopNavigationBar';
import MainHeader from '@/components/MainHeader';
import CategoryNavigation from '@/components/CategoryNavigation';
import ProductFilters from '@/components/ProductFilters';
import SortDropdown from '@/components/product/SortDropdown';
import Footer from '@/components/Footer';
import { useProducts } from '@/hooks/useProducts';
import { Skeleton } from '@/components/ui/skeleton';
import OptimizedImage from '@/components/OptimizedImage';

const PRODUCTS_PER_PAGE = 9;

const fetchProducts = async (filters: any) => {
  const queryParams = new URLSearchParams({
    limit: PRODUCTS_PER_PAGE.toString(),
    page: filters.page.toString(),
    sortBy: filters.sortBy,
    category: 'mens', // Specific to this page
    ...filters.applied
  });
  const response = await fetch(`/api/products?${queryParams.toString()}`);
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
  return response.json();
};

export default function MensProducts() {
  const [location, setLocation] = useLocation();
  const [appliedFilters, setAppliedFilters] = useState<Record<string, string[]>>({});
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('best-match');
  const [favorites, setFavorites] = useState<Set<number>>(new Set());

  const { data, isLoading, error } = useQuery({
    queryKey: ['products', 'mens', { page: currentPage, sortBy, applied: appliedFilters }],
    queryFn: () => fetchProducts({ page: currentPage, sortBy, applied: appliedFilters }),
    placeholderData: keepPreviousData,
  });

  const currentProducts = data?.products || [];
  const totalPages = data?.pagination.totalPages || 1;

  const toggleFavorite = (productId: number) => {
    setFavorites(prev => {
      const newFavorites = new Set(prev);
      if (newFavorites.has(productId)) {
        newFavorites.delete(productId);
      } else {
        newFavorites.add(productId);
      }
      return newFavorites;
    });
  };

  const handleFilterChange = (filters: Record<string, string[]>) => {
    setCurrentPage(1);
    setAppliedFilters(filters);
  };

  const handleSortChange = (sort: string) => {
    setCurrentPage(1);
    setSortBy(sort);
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`h-3 w-3 ${i < rating ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}`}
      />
    ));
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="sticky top-0 z-40 bg-background">
        <TopNavigationBar />
        <MainHeader />
        <CategoryNavigation />
      </div>

      <div className="bg-muted/30 py-8">
        <div className="max-w-7xl mx-auto px-4">
          <h1 className="text-3xl font-bold mb-2">Men's Medical Uniforms</h1>
          <p className="text-muted-foreground">
            Professional scrubs and medical wear designed for healthcare professionals.
          </p>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8 grid grid-cols-1 lg:grid-cols-4 gap-8">
        <aside className="lg:col-span-1">
          <div className="sticky top-36">
            <ProductFilters onFilterChange={handleFilterChange} />
          </div>
        </aside>

        <main className="lg:col-span-3">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-lg font-medium text-muted-foreground">
              {isLoading ? <Skeleton className="h-6 w-32" /> : `Showing ${data?.pagination.totalProducts.toLocaleString() || 0} items`}
            </h2>
            <SortDropdown onSortChange={handleSortChange} />
          </div>

          {isLoading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-6">
              {Array.from({ length: PRODUCTS_PER_PAGE }).map((_, i) => (
                <Card key={i}>
                  <Skeleton className="h-64 w-full" />
                  <div className="p-4 space-y-2">
                    <Skeleton className="h-4 w-3/4" />
                    <Skeleton className="h-4 w-1/2" />
                    <Skeleton className="h-6 w-full" />
                  </div>
                </Card>
              ))}
            </div>
          ) : error ? (
            <div className="text-center py-12 text-destructive">Failed to load products. Please try again.</div>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-6">
              {currentProducts.map((product: any) => (
                <Card
                  key={product.id}
                  className="overflow-hidden hover:shadow-lg transition-shadow cursor-pointer group"
                  onClick={() => setLocation(`/product/${product.id}`)}
                >
                  {/* Product Image */}
                    <div className="relative h-64 bg-muted overflow-hidden">
                      {product.images && product.images.length > 0 ? (
                        <OptimizedImage 
                          src={product.images[0].url} 
                          alt={product.name}
                          className="absolute inset-0 w-full h-full object-cover opacity-60"
                        />
                      ) : (
                        <div className="bg-gray-200 border-2 border-dashed rounded-xl w-full h-full" />
                      )}
                      <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent" />
                    <div className="absolute top-3 left-3 flex flex-col gap-1">
                      {product.isOnSale && <Badge variant="destructive">SALE</Badge>}
                      {product.isNew && <Badge>NEW</Badge>}
                    </div>
                    <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-opacity">
                      <Button size="sm" variant="secondary" className="h-8 w-8 p-0 rounded-full" onClick={(e) => { e.stopPropagation(); toggleFavorite(product.id); }}>
                        <Heart className={`h-4 w-4 ${favorites.has(product.id) ? 'fill-destructive text-destructive' : ''}`} />
                      </Button>
                    </div>
                  </div>
                  <div className="p-4">
                    <h3 className="font-semibold text-sm group-hover:text-primary transition-colors">{product.name}</h3>
                    <p className="text-xs text-muted-foreground">{product.brand}</p>
                    <div className="flex items-center gap-1 my-2">
                      <div className="flex">{renderStars(Math.floor(product.rating))}</div>
                      <span className="text-xs text-muted-foreground">({product.reviewCount})</span>
                    </div>
                    <div className="flex items-center gap-2 mb-3">
                      <span className="font-bold text-lg text-primary">${(product.price / 100).toFixed(2)}</span>
                      {product.originalPrice && <span className="text-sm text-muted-foreground line-through">${(product.originalPrice / 100).toFixed(2)}</span>}
                    </div>
                    <Button className="w-full" size="sm" onClick={(e) => e.stopPropagation()}>Add to Cart</Button>
                  </div>
                </Card>
              ))}
            </div>
          )}

          {totalPages > 1 && (
            <div className="mt-12 flex justify-center">
              <Pagination>
                <PaginationContent>
                  <PaginationItem>
                    <PaginationPrevious onClick={() => setCurrentPage(p => Math.max(p - 1, 1))} className={currentPage === 1 ? 'pointer-events-none opacity-50' : 'cursor-pointer'} />
                  </PaginationItem>
                  {[...Array(totalPages)].map((_, i) => (
                     <PaginationItem key={i}>
                       <PaginationLink onClick={() => setCurrentPage(i + 1)} isActive={currentPage === i + 1} className="cursor-pointer">{i + 1}</PaginationLink>
                     </PaginationItem>
                  ))}
                  <PaginationItem>
                    <PaginationNext onClick={() => setCurrentPage(p => Math.min(p + 1, totalPages))} className={currentPage === totalPages ? 'pointer-events-none opacity-50' : 'cursor-pointer'} />
                  </PaginationItem>
                </PaginationContent>
              </Pagination>
            </div>
          )}
        </main>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/NewArrivals.tsx.content:
import { useState } from 'react';
import { useLocation } from 'wouter';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Star, Heart, Search } from 'lucide-react';
import { 
  Pagination,
  PaginationContent,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious,
  PaginationEllipsis
} from '@/components/ui/pagination';
import TopNavigationBar from '@/components/TopNavigationBar';
import MainHeader from '@/components/MainHeader';
import CategoryNavigation from '@/components/CategoryNavigation';
import ProductFilters from '@/components/ProductFilters';
import SortDropdown from '@/components/product/SortDropdown';
import Footer from '@/components/Footer';
import { useProducts } from '@/hooks/useProducts';
import { Skeleton } from '@/components/ui/skeleton';
import OptimizedImage from '@/components/OptimizedImage';
import { sampleProducts } from '@/data/products';

const PRODUCTS_PER_PAGE = 8;

export default function NewArrivals() {
  const [location, setLocation] = useLocation();
  // Filter products to show only new arrivals
  const newProducts = sampleProducts.filter((product: any) => product.isNew);
  const [filteredProducts, setFilteredProducts] = useState(newProducts);
  const [appliedFilters, setAppliedFilters] = useState<Record<string, string[]>>({});
  const [currentPage, setCurrentPage] = useState(1);
  const [favorites, setFavorites] = useState<Set<number>>(new Set());

  const totalPages = Math.ceil(filteredProducts.length / PRODUCTS_PER_PAGE);
  const startIndex = (currentPage - 1) * PRODUCTS_PER_PAGE;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + PRODUCTS_PER_PAGE);

  const toggleFavorite = (productId: number) => {
    setFavorites(prev => {
      const newFavorites = new Set(prev);
      if (newFavorites.has(productId)) {
        newFavorites.delete(productId);
      } else {
        newFavorites.add(productId);
      }
      return newFavorites;
    });
  };

  const handleFilterChange = (filters: Record<string, string[]>) => {
    setAppliedFilters(filters);
    setCurrentPage(1);

    let filtered = [...newProducts];

    Object.entries(filters).forEach(([filterType, filterValues]) => {
      if (filterValues.length === 0) return;

      filtered = filtered.filter(product => {
        switch (filterType) {
          case 'brand':
            return filterValues.some(brandSlug => 
              product.brand.toLowerCase().replace(/[^a-z0-9]/g, '-') === brandSlug
            );
          case 'color':
            return filterValues.some(color =>
              product.colors.some(productColor =>
                productColor.toLowerCase().includes(color.toLowerCase())
              )
            );
          case 'size':
            return filterValues.some(size =>
              product.sizes.some(productSize =>
                productSize.toLowerCase() === size.toLowerCase()
              )
            );
          case 'sale':
            if (filterValues.includes('all-sale') && product.isOnSale) return true;
            return false;
          case 'category':
            if (filterValues.includes('tops') && product.name.toLowerCase().includes('top')) return true;
            if (filterValues.includes('pants') && product.name.toLowerCase().includes('pant')) return true;
            if (filterValues.includes('sets') && product.name.toLowerCase().includes('set')) return true;
            if (filterValues.includes('jackets') && product.name.toLowerCase().includes('jacket')) return true;
            return filterValues.length === 0;
          default:
            return true;
        }
      });
    });

    setFilteredProducts(filtered);
  };

  const handleSortChange = (sort: string) => {
    let sorted = [...filteredProducts];
    setCurrentPage(1);
    switch (sort) {
      case 'price-low':
        sorted.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        sorted.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        sorted.sort((a, b) => b.rating - a.rating);
        break;
      case 'reviews':
        sorted.sort((a, b) => b.reviewCount - a.reviewCount);
        break;
      case 'newest':
        sorted.sort((a, b) => b.id - a.id);
        break;
      default:
        break;
    }
    setFilteredProducts(sorted);
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`h-3 w-3 ${i < rating ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}`}
      />
    ));
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Navigation */}
      <div className="sticky top-0 z-40 bg-background">
        <TopNavigationBar />
        <MainHeader />
        <CategoryNavigation />
      </div>

      {/* Page Header */}
      <div className="bg-gradient-to-r from-primary/10 to-secondary/10 py-12">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <div className="flex items-center justify-center gap-2 mb-4">
            {/* <Sparkles className="h-8 w-8 text-primary" /> */}
            <h1 className="text-4xl font-bold">New Arrivals</h1>
            {/* <Sparkles className="h-8 w-8 text-primary" /> */}
          </div>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Discover the latest in medical uniforms and scrubs. Fresh styles, innovative fabrics, and cutting-edge designs for today's healthcare professionals.
          </p>
          <Badge className="mt-4 bg-primary/20 text-primary hover:bg-primary/30">
            {filteredProducts.length} New Products Available
          </Badge>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Filters */}
        <ProductFilters
          itemCount={filteredProducts.length}
          onFilterChange={handleFilterChange}
          onSortChange={handleSortChange}
        />

        {/* Product Grid */}
        <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {currentProducts.map((product: any) => (
            <Card
              key={product.id}
              className="overflow-hidden hover:shadow-lg transition-shadow cursor-pointer group"
              onClick={() => setLocation(`/product/${product.id}`)}
            >
              {/* Product Image */}
                    <div className="relative h-64 bg-muted overflow-hidden">
                      {product.images && product.images.length > 0 ? (
                        <OptimizedImage 
                          src={product.images[0].url} 
                          alt={product.name}
                          className="absolute inset-0 w-full h-full object-cover opacity-60"
                        />
                      ) : (
                        <div className="bg-gray-200 border-2 border-dashed rounded-xl w-full h-full" />
                      )}
                      <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent" />

                {/* Badges */}
                <div className="absolute top-3 left-3 flex flex-col gap-1">
                  <Badge className="bg-gradient-to-r from-primary to-secondary text-primary-foreground text-xs font-bold">
                    NEW
                  </Badge>
                  {product.isOnSale && (
                    <Badge variant="destructive" className="text-xs font-bold">
                      SALE
                    </Badge>
                  )}
                </div>

                {/* Favorite & Quick Actions */}
                <div className="absolute top-3 right-3 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <Button 
                    size="sm" 
                    variant="secondary"
                    className="h-8 w-8 p-0 rounded-full"
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleFavorite(product.id);
                    }}
                  >
                    <Heart 
                      className={`h-4 w-4 ${favorites.has(product.id) ? 'fill-destructive text-destructive' : ''}`} 
                    />
                  </Button>
                  <Button size="sm" variant="secondary" className="text-xs px-2">
                    Quick View
                  </Button>
                </div>
              </div>

              {/* Product Info */}
              <div className="p-4">
                <div className="mb-2">
                  <h3 className="font-semibold text-sm group-hover:text-primary transition-colors">
                    {product.name}
                  </h3>
                  <p className="text-xs text-muted-foreground">{product.brand}</p>
                </div>

                {/* Rating */}
                <div className="flex items-center gap-1 mb-2">
                  <div className="flex">{renderStars(Math.floor(product.rating))}</div>
                  <span className="text-xs text-muted-foreground">
                    {product.rating} ({product.reviewCount})
                  </span>
                </div>

                {/* Price */}
                <div className="flex items-center gap-2 mb-3">
                  <span className="font-bold text-lg text-primary">
                    ${(product.price / 100).toFixed(2)}
                  </span>
                  {product.originalPrice && (
                    <>
                      <span className="text-sm text-muted-foreground line-through">
                        ${(product.originalPrice / 100).toFixed(2)}
                      </span>
                      <Badge variant="destructive" className="text-xs">
                        {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                      </Badge>
                    </>
                  )}
                </div>

                {/* Colors */}
                <div className="flex items-center gap-1 mb-3">
                  <span className="text-xs text-muted-foreground">Colors:</span>
                  <div className="flex gap-1">
                    {product.colors.map((color: any, index: any) => (
                      <div
                        key={index}
                        className="w-3 h-3 rounded-full bg-gray-400 border"
                        title={color}
                      />
                    ))}
                    {product.colors.length > 3 && (
                      <span className="text-xs text-muted-foreground">
                        +{product.colors.length - 3}
                      </span>
                    )}
                  </div>
                </div>

                {/* Add to Cart */}
                <Button
                  className="w-full"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    console.log(`Add to cart: ${product.name}`);
                  }}
                >
                  Add to Cart
                </Button>
              </div>
            </Card>
          ))}
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-12 flex justify-center">
            <Pagination>
              <PaginationContent>
                <PaginationItem>
                  <PaginationPrevious 
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    className={currentPage === 1 ? 'pointer-events-none opacity-50' : 'cursor-pointer'}
                  />
                </PaginationItem>

                {/* First page */}
                {currentPage > 3 && (
                  <>
                    <PaginationItem>
                      <PaginationLink onClick={() => setCurrentPage(1)} className="cursor-pointer">
                        1
                      </PaginationLink>
                    </PaginationItem>
                    {currentPage > 4 && (
                      <PaginationItem>
                        <PaginationEllipsis />
                      </PaginationItem>
                    )}
                  </>
                )}

                {/* Current page range */}
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  let pageNum;
                  if (totalPages <= 5) {
                    pageNum = i + 1;
                  } else if (currentPage <= 3) {
                    pageNum = i + 1;
                  } else if (currentPage >= totalPages - 2) {
                    pageNum = totalPages - 4 + i;
                  } else {
                    pageNum = currentPage - 2 + i;
                  }

                  if (pageNum > 0 && pageNum <= totalPages) {
                    return (
                      <PaginationItem key={pageNum}>
                        <PaginationLink 
                          onClick={() => setCurrentPage(pageNum)}
                          isActive={currentPage === pageNum}
                          className="cursor-pointer"
                        >
                          {pageNum}
                        </PaginationLink>
                      </PaginationItem>
                    );
                  }
                  return null;
                })}

                {/* Last page */}
                {currentPage < totalPages - 2 && (
                  <>
                    {currentPage < totalPages - 3 && (
                      <PaginationItem>
                        <PaginationEllipsis />
                      </PaginationItem>
                    )}
                    <PaginationItem>
                      <PaginationLink onClick={() => setCurrentPage(totalPages)} className="cursor-pointer">
                        {totalPages}
                      </PaginationLink>
                    </PaginationItem>
                  </>
                )}

                <PaginationItem>
                  <PaginationNext 
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                    className={currentPage === totalPages ? 'pointer-events-none opacity-50' : 'cursor-pointer'}
                  />
                </PaginationItem>
              </PaginationContent>
            </Pagination>
          </div>
        )}

        {/* Empty state */}
        {filteredProducts.length === 0 && (
          <div className="text-center py-16">
            {/* <Sparkles className="h-16 w-16 text-muted-foreground mx-auto mb-4" /> */}
            <h3 className="text-2xl font-bold text-center mb-2">No Products Found</h3>
          </div>
        )}

        {/* Results Info */}
        <div className="mt-8 text-center text-sm text-muted-foreground">
          Showing {Math.min(startIndex + 1, filteredProducts.length)}-{Math.min(startIndex + PRODUCTS_PER_PAGE, filteredProducts.length)} of {filteredProducts.length} new products
        </div>
      </div>

      <Footer />
    </div>
  );
}

