      query: q as string,
      type: type as string,
      page: pageNum,
      limit: limitNum
    });

    res.json(results);
  } catch (error) {
    let errorMessage = "An unexpected error occurred.";
    if (error instanceof Error) {
      errorMessage = error.message;
    }
    res.status(500).json({ message: errorMessage });
  }
});

export default router;

