      </Card>
    </div>
  );
}

client/src/pages/AddProduct.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { 
  ArrowLeft,
  Upload,
  Plus,
  X
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { useUploadImage } from "@/hooks/admin/useAdmin";
import { handleApiError, handleSuccess } from "@/lib/errorHandler";
import { useTrackEvent } from "@/hooks/useTracking";

export default function AddProduct() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [brand, setBrand] = useState("");
  const [sellingPrice, setSellingPrice] = useState("");
  const [discountPrice, setDiscountPrice] = useState("");
  const [stockQuantity, setStockQuantity] = useState("");
  const [minimumOrder, setMinimumOrder] = useState("1");
  const [thumbnail, setThumbnail] = useState<File | null>(null);
  const [thumbnailPreview, setThumbnailPreview] = useState<string | null>(null);
  const [selectedColors, setSelectedColors] = useState<string[]>([]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);
  const [colorImages, setColorImages] = useState<Record<string, {file: File | null, preview: string | null}>>({});

  const uploadImageMutation = useUploadImage();
  const { trackEvent } = useTrackEvent();

  const categories = [
    { id: 1, name: "Scrubs" },
    { id: 2, name: "Lab Coats" },
    { id: 3, name: "Shoes" },
    { id: 4, name: "Accessories" },
  ];

  const subCategories = [
    { id: 1, name: "Men's Scrubs", categoryId: 1 },
    { id: 2, name: "Women's Scrubs", categoryId: 1 },
    { id: 3, name: "Children's Lab Coats", categoryId: 2 },
    { id: 4, name: "Surgical Shoes", categoryId: 3 },
  ];

  const brands = [
    { id: 1, name: "MediWear" },
    { id: 2, name: "NursePro" },
    { id: 3, name: "ComfortFeet" },
    { id: 4, name: "SafeGuard" },
  ];

  const availableColors = [
    { name: "Black", value: "#000000" },
    { name: "White", value: "#FFFFFF" },
    { name: "Blue", value: "#0000FF" },
    { name: "Green", value: "#00FF00" },
    { name: "Red", value: "#FF0000" },
    { name: "Pink", value: "#FFC0CB" },
  ];

  const availableSizes = ["XS", "S", "M", "L", "XL", "XXL", "3XL"];

  const filteredSubCategories = subCategories.filter(
    (sub) => category === "" || sub.categoryId === parseInt(category)
  );

  const handleThumbnailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setThumbnail(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setThumbnailPreview(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleColorSelect = (colorName: string) => {
    setSelectedColors(prev => {
      if (prev.includes(colorName)) {
        // Remove color and its images
        const newColors = prev.filter(c => c !== colorName);
        setColorImages(prevImages => {
          const newImages = {...prevImages};
          delete newImages[colorName];
          return newImages;
        });
        return newColors;
      } else {
        // Add color
        setColorImages(prev => ({
          ...prev,
          [colorName]: {file: null, preview: null}
        }));
        return [...prev, colorName];
      }
    });
  };

  const handleSizeSelect = (size: string) => {
    setSelectedSizes(prev => {
      if (prev.includes(size)) {
        return prev.filter(s => s !== size);
      } else {
        return [...prev, size];
      }
    });
  };

  const handleColorImageChange = (colorName: string, e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setColorImages(prev => ({
          ...prev,
          [colorName]: {
            file: file,
            preview: e.target?.result as string
          }
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      // Upload thumbnail image if available
      let thumbnailUrl = null;
      if (thumbnail) {
        const imageData = await uploadImageMutation.mutateAsync(thumbnail);
        thumbnailUrl = imageData.url;
      }

      // Upload color images if available
      const colorImageUrls: Record<string, string> = {};
      for (const [colorName, imageData] of Object.entries(colorImages)) {
        if (imageData.file) {
          const uploadedImage = await uploadImageMutation.mutateAsync(imageData.file);
          colorImageUrls[colorName] = uploadedImage.url;
        }
      }

      // Prepare product data
      const productData = {
        name,
        description,
        categoryId: category,
        brandId: brand,
        price: parseFloat(sellingPrice),
        comparePrice: discountPrice ? parseFloat(discountPrice) : null,
        inventoryQuantity: parseInt(stockQuantity) || 0,
        minimumOrder: parseInt(minimumOrder) || 1,
        thumbnailUrl,
        colorImageUrls,
        selectedColors,
        selectedSizes
      };

      console.log("Product data to submit:", productData);

      // Track product creation
      trackEvent('create_product', 'admin', name);

      // In a real app, you would send this to your backend API
      handleSuccess("Product Created!", "The product has been created successfully.");
    } catch (error) {
      handleApiError(error, "Failed to create product");
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <Button variant="outline" asChild className="mb-4">
          <Link to="/admin/products">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Products
          </Link>
        </Button>
        <h1 className="text-2xl font-bold text-gray-900">Add Product</h1>
        <p className="mt-2 text-gray-700">
          Create a new product for your store.
        </p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        <Card>
          <CardHeader>
            <CardTitle>Basic Information</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="space-y-2">
              <Label htmlFor="name">Product Name *</Label>
              <Input
                id="name"
                placeholder="Enter product name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Enter product description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                rows={4}
              />
            </div>
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle>Organization</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="category">Category *</Label>
                <Select value={category} onValueChange={setCategory}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select a category" />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((cat) => (
                      <SelectItem key={cat.id} value={cat.id.toString()}>
                        {cat.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="subCategory">Sub-Category</Label>
                <Select 
                  value={subCategory} 
                  onValueChange={setSubCategory}
                  disabled={category === ""}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select a sub-category" />
                  </SelectTrigger>
                  <SelectContent>
                    {filteredSubCategories.map((sub) => (
                      <SelectItem key={sub.id} value={sub.id.toString()}>
                        {sub.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="brand">Brand *</Label>
                <Select value={brand} onValueChange={setBrand}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select a brand" />
                  </SelectTrigger>
                  <SelectContent>
                    {brands.map((b) => (
                      <SelectItem key={b.id} value={b.id.toString()}>
                        {b.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Pricing & Inventory</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="sellingPrice">Selling Price *</Label>
                  <Input
                    id="sellingPrice"
                    type="number"
                    placeholder="0.00"
                    value={sellingPrice}
                    onChange={(e) => setSellingPrice(e.target.value)}
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="discountPrice">Discount Price</Label>
                  <Input
                    id="discountPrice"
                    type="number"
                    placeholder="0.00"
                    value={discountPrice}
                    onChange={(e) => setDiscountPrice(e.target.value)}
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="stockQuantity">Stock Quantity</Label>
                  <Input
                    id="stockQuantity"
                    type="number"
                    placeholder="0"
                    value={stockQuantity}
                    onChange={(e) => setStockQuantity(e.target.value)}
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="minimumOrder">Minimum Order</Label>
                  <Input
                    id="minimumOrder"
                    type="number"
                    placeholder="1"
                    value={minimumOrder}
                    onChange={(e) => setMinimumOrder(e.target.value)}
                  />
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Product Media</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="space-y-2">
              <Label>Thumbnail Image</Label>
              <div className="flex items-start gap-6">
                <div className="flex flex-col items-center gap-2">
                  {thumbnailPreview ? (
                    <img
                      src={thumbnailPreview}
                      alt="Thumbnail preview"
                      className="h-32 w-32 rounded-md object-cover border"
                    />
                  ) : (
                    <div className="h-32 w-32 rounded-md bg-gray-100 border flex items-center justify-center">
                      <Upload className="h-8 w-8 text-gray-400" />
                    </div>
                  )}
                  <p className="text-xs text-gray-500 text-center">
                    JPG, PNG, GIF (Max 2MB)
                  </p>
                </div>

                <div className="flex-1">
                  <Input
                    type="file"
                    accept="image/*"
                    onChange={handleThumbnailChange}
                    className="hidden"
                    id="thumbnail-upload"
                  />
                  <Label
                    htmlFor="thumbnail-upload"
                    className="cursor-pointer inline-flex items-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                  >
                    <Upload className="mr-2 h-4 w-4" />
                    Upload Image
                  </Label>
                  <p className="mt-2 text-sm text-gray-500">
                    Upload a thumbnail for this product. Recommended size: 600x600px
                  </p>
                </div>
              </div>
            </div>

            <div className="space-y-2">
              <Label>Colors</Label>
              <div className="flex flex-wrap gap-2">
                {availableColors.map((color) => (
                  <div key={color.name} className="flex items-center">
                    <Checkbox
                      id={`color-${color.name}`}
                      checked={selectedColors.includes(color.name)}
                      onCheckedChange={() => handleColorSelect(color.name)}
                    />
                    <Label 
                      htmlFor={`color-${color.name}`} 
                      className="ml-2 flex items-center gap-2"
                    >
                      <div 
                        className="h-4 w-4 rounded-full border" 
                        style={{ backgroundColor: color.value }}
                      />
                      {color.name}
                    </Label>
                  </div>
                ))}
              </div>

              {selectedColors.length > 0 && (
                <div className="mt-4 space-y-4">
                  <h4 className="font-medium">Color Images</h4>
                  {selectedColors.map((color) => {
                    const colorData = availableColors.find(c => c.name === color);
                    return (
                      <div key={color} className="flex items-center gap-4 p-3 border rounded-md">
                        <div 
                          className="h-6 w-6 rounded-full border" 
                          style={{ backgroundColor: colorData?.value }}
                        />
                        <span className="font-medium">{color}</span>

                        <div className="flex-1 flex items-center gap-4">
                          {colorImages[color]?.preview ? (
                            <img
                              src={colorImages[color].preview!}
                              alt={`${color} preview`}
                              className="h-16 w-16 rounded-md object-cover border"
                            />
                          ) : (
                            <div className="h-16 w-16 rounded-md bg-gray-100 border flex items-center justify-center">
                              <Upload className="h-4 w-4 text-gray-400" />
                            </div>
                          )}

                          <div>
                            <Input
                              type="file"
                              accept="image/*"
                              onChange={(e) => handleColorImageChange(color, e)}
                              className="hidden"
                              id={`color-image-${color}`}
                            />
                            <Label
                              htmlFor={`color-image-${color}`}
                              className="cursor-pointer inline-flex items-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                            >
                              <Upload className="mr-2 h-4 w-4" />
                              Upload
                            </Label>
                          </div>

                          <Button 
                            type="button"
                            variant="outline" 
                            size="icon"
                            onClick={() => {
                              setColorImages(prev => ({
                                ...prev,
                                [color]: {file: null, preview: null}
                              }));
                            }}
                          >
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>

            <div className="space-y-2">
              <Label>Sizes</Label>
              <div className="flex flex-wrap gap-2">
                {availableSizes.map((size) => (
                  <div key={size} className="flex items-center">
                    <Checkbox
                      id={`size-${size}`}
                      checked={selectedSizes.includes(size)}
                      onCheckedChange={() => handleSizeSelect(size)}
                    />
                    <Label htmlFor={`size-${size}`} className="ml-2">
                      {size}
                    </Label>
                  </div>
                ))}
              </div>
            </div>
          </CardContent>
        </Card>

        <div className="flex justify-end gap-3">
          <Button variant="outline" asChild>
            <Link to="/admin/products">
              Cancel
            </Link>
          </Button>
          <Button 
            type="submit" 
            disabled={uploadImageMutation.isPending}
          >
            {uploadImageMutation.isPending ? "Saving..." : "Save Product"}
          </Button>
        </div>
      </form>
    </div>
  );
}

client/src/pages/AddSubCategory.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { 
  ArrowLeft,
  Upload
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

export default function AddSubCategory() {
  const [name, setName] = useState("");
  const [parentCategory, setParentCategory] = useState("");
  const [description, setDescription] = useState("");
  const [thumbnail, setThumbnail] = useState<File | null>(null);
  const [thumbnailPreview, setThumbnailPreview] = useState<string | null>(null);

  const parentCategories = [
    { id: 1, name: "Scrubs" },
    { id: 2, name: "Lab Coats" },
    { id: 3, name: "Shoes" },
    { id: 4, name: "Accessories" },
    { id: 5, name: "Nursing Uniforms" },
  ];

  const handleThumbnailChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setThumbnail(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setThumbnailPreview(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // In a real app, this would submit the form data to the server
    console.log({ name, parentCategory, description, thumbnail });
  };

  return (
    <div className="space-y-6">
      <div>
        <Button variant="outline" asChild className="mb-4">
          <Link to="/admin/subcategories">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Sub-Categories
          </Link>
        </Button>
        <h1 className="text-2xl font-bold text-gray-900">Add Sub-Category</h1>
        <p className="mt-2 text-gray-700">
          Create a new product sub-category for your store.
        </p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Sub-Category Details</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-2">
              <Label htmlFor="name">Sub-Category Name *</Label>
              <Input
                id="name"
                placeholder="Enter sub-category name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="parentCategory">Parent Category *</Label>
              <Select value={parentCategory} onValueChange={setParentCategory}>
                <SelectTrigger>
                  <SelectValue placeholder="Select a parent category" />
                </SelectTrigger>
                <SelectContent>
                  {parentCategories.map((category) => (
                    <SelectItem key={category.id} value={category.id.toString()}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Enter sub-category description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                rows={4}
              />
            </div>

            <div className="space-y-2">
              <Label>Thumbnail</Label>
              <div className="flex items-start gap-6">
                <div className="flex flex-col items-center gap-2">
                  {thumbnailPreview ? (
                    <img
                      src={thumbnailPreview}
                      alt="Thumbnail preview"
                      className="h-32 w-32 rounded-md object-cover border"
                    />
                  ) : (
                    <div className="h-32 w-32 rounded-md bg-gray-100 border flex items-center justify-center">
                      <Upload className="h-8 w-8 text-gray-400" />
                    </div>
                  )}
                  <p className="text-xs text-gray-500 text-center">
                    JPG, PNG, GIF (Max 2MB)
                  </p>
                </div>

                <div className="flex-1">
                  <Input
                    type="file"
                    accept="image/*"
                    onChange={handleThumbnailChange}
                    className="hidden"
                    id="thumbnail-upload"
                  />
                  <Label
                    htmlFor="thumbnail-upload"
                    className="cursor-pointer inline-flex items-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                  >
                    <Upload className="mr-2 h-4 w-4" />
                    Upload Image
                  </Label>
                  <p className="mt-2 text-sm text-gray-500">
                    Upload a thumbnail for this sub-category. Recommended size: 300x300px
                  </p>
                </div>
              </div>
            </div>

            <div className="flex justify-end gap-3">
              <Button variant="outline" asChild>
                <Link to="/admin/subcategories">
                  Cancel
                </Link>
              </Button>
              <Button type="submit">
                Save Sub-Category
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/AdminDashboard.tsx.content:
import { useState } from "react";
import { Link, Route, Switch, useLocation } from "wouter";
import { 
  LayoutDashboard, 
  ShoppingCart, 
  Package, 
  Users, 
  BarChart3, 
  MessageSquare, 
  Settings,
  LogOut,
  Menu,
  Search,
  Bell,
  User,
  Ticket,
  FileText,
  Star,
  Archive,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { cn } from "@/lib/utils";
import { Input } from "@/components/ui/input";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

// Import admin components and hooks
import DashboardContent from "@/components/admin/DashboardContent";
import { useAdminAuth, withAdminAuth } from "@/contexts/AdminAuthContext";
import OrderManagement from "./OrderManagement";
import OrderDetails from "./OrderDetails";

// Import real admin management components
import AllProducts from "./AllProducts";
import AllCategories from "./AllCategories";
import CustomerManagement from "./CustomerManagement";
import CouponManagement from "./CouponManagement";
import BlogPostManagement from "./BlogPostManagement";
import ReviewManagement from "./ReviewManagement";
import CollectionManagement from "./CollectionManagement";
import RefundManagement from "./RefundManagement";
import MessagesPage from "./MessagesPage";

// All management components are now imported as real functional components

const navigation = [
  { name: "Dashboard", href: "/", icon: LayoutDashboard },
  { name: "Orders", href: "/orders", icon: ShoppingCart },
  { name: "Products", href: "/products", icon: Package },
  { name: "Categories", href: "/categories", icon: Users },
  { name: "Customers", href: "/customers", icon: Users },
  { name: "Coupons", href: "/coupons", icon: Ticket },
  { name: "Blog Posts", href: "/blog-posts", icon: FileText },
  { name: "Reviews", href: "/reviews", icon: Star },
  { name: "Collections", href: "/collections", icon: Archive },
  { name: "Refunds", href: "/refunds", icon: MessageSquare },
  { name: "Messages", href: "/messages", icon: Settings },
];

function AdminDashboard() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [location, setLocation] = useLocation();
  const { user, logout } = useAdminAuth();


  // Get the current path for active link detection
  const currentPath = location.startsWith('/admin') ? location.substring(6) || '/' : '/';

  const handleLogout = () => {
    logout();
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Mobile sidebar */}
      <Sheet open={sidebarOpen} onOpenChange={setSidebarOpen}>
        <SheetTrigger asChild>
          <Button 
            variant="outline" 
            size="icon" 
            className="fixed top-4 left-4 z-50 bg-white lg:hidden"
            aria-label="Open navigation menu"
          >
            <Menu className="h-4 w-4" />
          </Button>
        </SheetTrigger>
        <SheetContent side="left" className="w-64 p-0">
          <div className="flex h-full flex-col">
            <div className="flex h-16 items-center border-b px-4">
              <h1 className="text-xl font-bold text-primary">DEV Egypt Admin</h1>
            </div>
            <nav className="flex-1 space-y-1 p-4" aria-label="Main navigation">
              {navigation.map((item) => {
                const Icon = item.icon;
                const isActive = currentPath === item.href;
                return (
                  <Link
                    key={item.name}
                    to={`/admin${item.href}`}
                    className={cn(
                      "flex items-center rounded-lg px-3 py-2 text-sm font-medium transition-colors",
                      isActive
                        ? "bg-primary text-primary-foreground"
                        : "text-gray-700 hover:bg-gray-100"
                    )}
                    onClick={() => setSidebarOpen(false)}
                  >
                    <Icon className="mr-3 h-5 w-5" aria-hidden="true" />
                    {item.name}
                  </Link>
                );
              })}
            </nav>
            <div className="border-t p-4">
              <Button
                variant="ghost"
                onClick={handleLogout}
                className="w-full justify-start text-gray-700 hover:bg-gray-100"
              >
                <LogOut className="mr-3 h-5 w-5" />
                Logout
              </Button>
            </div>
          </div>
        </SheetContent>
      </Sheet>

      {/* Desktop sidebar */}
      <div className="hidden lg:fixed lg:inset-y-0 lg:z-50 lg:flex lg:w-64 lg:flex-col">
        <div className="flex grow flex-col gap-y-5 overflow-y-auto border-r border-gray-200 bg-white px-6">
          <div className="flex h-16 shrink-0 items-center">
            <h1 className="text-xl font-bold text-primary">DEV Egypt Admin</h1>
          </div>
          <nav className="flex flex-1 flex-col" aria-label="Main navigation">
            <ul role="list" className="flex flex-1 flex-col gap-y-7">
              <li>
                <ul className="-mx-2 space-y-1">
                  {navigation.map((item) => {
                    const Icon = item.icon;
                    const isActive = currentPath === item.href;
                    return (
                      <li key={item.name}>
                        <Link
                          to={`/admin${item.href}`}
                          className={cn(
                            "group flex gap-x-3 rounded-md p-2 text-sm font-semibold leading-6 transition-colors",
                            isActive
                              ? "bg-primary text-white"
                              : "text-gray-700 hover:bg-gray-50"
                          )}
                        >
                          <Icon
                            className={cn(
                              "h-5 w-5 shrink-0",
                              isActive ? "text-white" : "text-gray-400 group-hover:text-primary"
                            )}
                            aria-hidden="true"
                          />
                          {item.name}
                        </Link>
                      </li>
                    );
                  })}
                </ul>
              </li>
              <li className="-mx-6 mt-auto">
                <Button
                  variant="ghost"
                  onClick={handleLogout}
                  className="flex w-full items-center gap-x-3 rounded-md p-3 text-sm font-semibold leading-6 text-gray-700 hover:bg-gray-50 transition-colors"
                  aria-label="Logout"
                >
                  <LogOut className="h-5 w-5 shrink-0 text-gray-400" aria-hidden="true" />
                  Logout
                </Button>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      {/* Main content */}
      <div className="lg:pl-64 w-full">
        {/* Top navigation bar */}
        <div className="sticky top-0 z-40 bg-white border-b border-gray-200">
          <div className="flex h-16 items-center px-4 sm:px-6 lg:px-8">
            <div className="flex flex-1 gap-x-4 self-stretch lg:gap-x-6">
              <form className="flex flex-1" onSubmit={(e) => e.preventDefault()}>
                <div className="relative w-full">
                  <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-gray-500" />
                  <Input
                    type="search"
                    placeholder="Search..."
                    className="w-full appearance-none bg-white pl-8 shadow-none border-gray-300 focus:border-primary transition-colors"
                  />
                </div>
              </form>
              <div className="flex items-center gap-x-4 lg:gap-x-6">
                <Button variant="ghost" size="icon" className="relative">
                  <Bell className="h-5 w-5" />
                  <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-red-500"></span>
                </Button>

                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="relative h-8 w-8 rounded-full">
                      <div className="bg-primary text-white rounded-full w-8 h-8 flex items-center justify-center text-sm font-semibold">
                        {user?.fullName?.charAt(0)?.toUpperCase() || 'A'}
                      </div>
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent className="w-56" align="end" forceMount>
                    <DropdownMenuLabel className="font-normal">
                      <div className="flex flex-col space-y-1">
                        <p className="text-sm font-medium leading-none">{user?.fullName || 'Admin User'}</p>
                        <p className="text-xs leading-none text-muted-foreground">{user?.email || 'admin@devegypt.com'}</p>
                      </div>
                    </DropdownMenuLabel>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem>
                      <User className="mr-2 h-4 w-4" />
                      <span>Profile</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem>
                      <Settings className="mr-2 h-4 w-4" />
                      <span>Settings</span>
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem onClick={handleLogout}>
                      <LogOut className="mr-2 h-4 w-4" />
                      <span>Log out</span>
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </div>
          </div>
        </div>

        <main className="py-6">
          <div className="px-4 sm:px-6 lg:px-8">
            <Switch>
              <Route path="/admin" component={DashboardContent} />
              <Route path="/admin/" component={DashboardContent} />
              <Route path="/admin/orders" component={OrderManagement} />
              <Route path="/admin/orders*" component={OrderManagement} />
              <Route path="/admin/products" component={AllProducts} />
              <Route path="/admin/categories" component={AllCategories} />
              <Route path="/admin/customers" component={CustomerManagement} />
              <Route path="/admin/coupons" component={CouponManagement} />
              <Route path="/admin/blog-posts" component={BlogPostManagement} />
              <Route path="/admin/reviews" component={ReviewManagement} />
              <Route path="/admin/collections" component={CollectionManagement} />
              <Route path="/admin/refunds" component={RefundManagement} />
              <Route path="/admin/messages" component={MessagesPage} />
              <Route>
                <div className="p-6">
                  <h2 className="text-2xl font-bold mb-4">Page Not Found</h2>
                  <p>Current path: {location}</p>
                  <p>No matching route found for this admin page.</p>
                </div>
              </Route>
            </Switch>
          </div>
        </main>
      </div>
    </div>
  );
}

// Export with authentication protection
export default withAdminAuth(AdminDashboard);

client/src/pages/AllCategories.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { 
  Search, 
  Plus,
  Edit,
  Trash2,
  Eye,
  Loader2,
  RefreshCw
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useCategories, useDeleteCategory } from "@/hooks/admin/useAdmin";
import type { Category } from "../../shared/schema";

export default function AllCategories() {
  const [searchTerm, setSearchTerm] = useState("");
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [categoryToDelete, setCategoryToDelete] = useState<string | null>(null);

  // Fetch categories using the real API
  const { data: categoriesResponse, isLoading, error } = useCategories();
  const deleteCategoryMutation = useDeleteCategory();

  // Extract categories from API response
  const categories = categoriesResponse?.categories || [];

  const filteredCategories = categories.filter((category: Category) => 
    category.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <Loader2 className="h-8 w-8 animate-spin" />
        <span className="ml-2 text-lg">Loading categories...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[400px] space-y-4">
        <p className="text-red-600 text-lg">Error loading categories: {error.message}</p>
        <Button onClick={() => window.location.reload()} variant="outline">
          <RefreshCw className="h-4 w-4 mr-2" />
          Try Again
        </Button>
      </div>
