  }

  async removeProductFromCollection(collectionId: string, productId: string) {
    return this.request(`/collections/${collectionId}/products/${productId}`, {
      method: 'DELETE',
    });
  }

  // Upload Image (alias for uploadFile)
  async uploadImage(file: File, onProgress?: (progress: number) => void) {
    return this.uploadFile(file, onProgress);
  }

  // File Upload
  async uploadFile(file: File, onProgress?: (progress: number) => void) {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const token = localStorage.getItem('admin_token');
      const xhr = new XMLHttpRequest();

      return new Promise<ApiResponse<{ url: string; originalName: string; size: number }>>(
        (resolve, reject) => {
          xhr.upload.onprogress = (event) => {
            if (event.lengthComputable && onProgress) {
              const progress = (event.loaded / event.total) * 100;
              onProgress(progress);
            }
          };

          xhr.onload = () => {
            try {
              const response = JSON.parse(xhr.responseText);
              if (xhr.status >= 200 && xhr.status < 300) {
                resolve(response);
              } else {
                resolve({
                  success: false,
                  error: response.error || 'Upload failed',
                });
              }
            } catch (error) {
              resolve({
                success: false,
                error: 'Failed to parse response',
              });
            }
          };

          xhr.onerror = () => {
            resolve({
              success: false,
              error: 'Network error during upload',
            });
          };

          xhr.open('POST', `${this.baseUrl}/upload`);
          if (token) {
            xhr.setRequestHeader('Authorization', `Bearer ${token}`);
          }
          xhr.send(formData);
        }
      );
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Upload failed',
      };
    }
  }

  // Bulk Operations
  async bulkDeleteProducts(productIds: string[]) {
    return this.request('/products/bulk-delete', {
      method: 'POST',
      body: JSON.stringify({ ids: productIds }),
    });
  }

  async bulkUpdateProductStatus(productIds: string[], isAvailable: boolean) {
    return this.request('/products/bulk-status', {
      method: 'PATCH',
      body: JSON.stringify({ ids: productIds, isAvailable }),
    });
  }

  async exportData(type: 'products' | 'orders' | 'customers', format: 'csv' | 'xlsx' = 'csv') {
    const response = await fetch(`${this.baseUrl}/export/${type}?format=${format}`, {
      headers: this.getAuthHeaders(),
    });

    if (response.ok) {
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${type}_export.${format}`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
      return { success: true };
    } else {
      const error = await response.json();
      return { success: false, error: error.message || 'Export failed' };
    }
  }
}

export const adminApi = new AdminApiClient();
export default adminApi;

