                </div>

                <div>
                  <span className="text-gray-600">Address Line 2:</span>
                  <p className="font-medium">{orderData.shipping.addressLine2}</p>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

