// Improved Admin API service functions with better error handling
import { apiRequest } from "@/lib/queryClient";

// Generic API response type
interface ApiResponse<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  message?: string;
}

// Pagination response type
interface PaginatedResponse<T = any> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasNext: boolean;
    hasPrev: boolean;
  };
}

// Helper function to handle API responses
async function handleApiResponse<T>(response: Response): Promise<T> {
  const result = await response.json();

  if (!response.ok || !result.success) {
    throw new Error(result.error || result.message || 'An error occurred');
  }

  return result.data;
}

// Helper function for paginated responses
async function handlePaginatedResponse<T>(response: Response): Promise<PaginatedResponse<T>> {
  const result = await response.json();

  if (!response.ok || !result.success) {
    throw new Error(result.error || result.message || 'An error occurred');
  }

  return result.data;
}


