                  {/* Egyptian Flag Indicator */}
                  {brand.isEgyptian && (
                    <div className="absolute top-2 right-2 bg-secondary text-white px-2 py-1 rounded text-xs font-semibold">
                      üá™üá¨ Made in Egypt
