                  <div className="flex items-center justify-between mb-2">
                    <h3 className="text-lg font-bold group-hover:text-primary transition-colors">
                      {brand.name}
                    </h3>
                    <span className="text-xs bg-accent text-accent-foreground px-2 py-1 rounded">
                      {brand.category}
                    </span>
                  </div>
